mapper.select:
SELECT count(*) FROM t_book


        bookname LIKE CONCAT('%',#{bookname},'%')


        and typeid = #{type}


        and publisher = #{publisher}

SELECT t_book.*, t_booktype.id as 'bookType.id',t_booktype.booktype as 'bookType.bookType', t_publisher.id as 'publisher.id',t_publisher.pubname as 'publisher.pubname' FROM t_book INNER join t_booktype ON t_book.typeid = t_booktype.id INNER JOIN t_publisher on t_book.pubid = t_publisher.id
bookname LIKE CONCAT('%',#{bookname},'%')
and typeid = #{type}
and publisher = #{publisher}
ORDER by t_book.id DESC limit ${start},${size}
